mapper.delete:
DELETE
	FROM FOLLOW
WHERE FOLLOW_MEMBER_NO = #{followerMno}
AND
	FOLLOW_TARGET_MEMBER_NO = #{mNo}

DELETE
	FROM "COMMENT"
WHERE
	COMMENT_WRITER_MEMBER_NO = #{writerNo}
	AND
	COMMENT_NO = #{commentNo}

mapper.insert:


	SELECT SEQ_MEMBER_PROFILE_IMAGE_NO.NEXTVAL FROM DUAL


INSERT INTO PROFILE_IMAGE
VALUES(
	#{profileImageNo},
	#{profileImagePath}
)



			SELECT SEQ_MEMBER_BANNER_NO.NEXTVAL FROM DUAL


   		INSERT INTO MEMBER_BANNER
   		VALUES(
			#{memberBannerNo},
			#{memberBannerPath}
		)

mapper.update:
UPDATE MEMBER
SET MEMBER_STATUS = 'N'
WHERE MEMBER_NO =#{mNo}
